targets.expr:
sum(
# map state to a numeric value
# since grafana doesn't support value mapping yet
  label_replace(
    label_replace(
      label_replace(
       kong_upstream_target_health{upstream=~"$upstream"}
       # healthy is positive number
       , "state_value", "1", "state", "healthy"
       # healthchecks_off is 0
      ), "state_value", "0", "state", "healthchecks_off"
      # unhealthy is negative number
    ), "state_value", "-1", "state", "(dns_error|unhealthy)"
  )
)
by (upstream, target, address, state, state_value) > 0
